    try {
        const pool = await sql.connect(dbSettings);
        //const result = await pool.request().query("SELECT * FROM USUARIO");    
        console.log('POOL: '+JSON.stringify(pool))
        return pool;
    }
    catch (error){
        console.log(error);
    }    
};

export {sql};
